import { useLocation } from 'react-router-dom';
import React, { useEffect, useState } from 'react';
import style from '../../Style/Transferencia/Transferencia.module.css';

const Edicao = () => {
    const [form, setForm] = useState({ documento: '', agencia: '', conta: '', valor: '' });
    const [errorMessage, setErrorMessage] = useState('');

    const location = useLocation();
    const numeroConta  = location.state || { numeroConta: '' };
    
    useEffect(() => {
        fetchData(numeroConta);
    }, []);
    
    const fetchData = async (id) => {
        try {
            const response = await fetch(`http://localhost:8080/transacao/conta/${String(numeroConta)}`, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json'
                },
            });
            const data = await response.json();
            form.documento = data.documento
            form.agencia = data.numero_agencia
            form.conta = data.numero_conta
            form.valor = data.limite_pix
        } catch (error) {
            console.error('Erro ao buscar dados da API:', error);
        }
    };

    const handleInputChange = (e, formSetter) => {
        const { name, value } = e.target;
        let formattedValue = value.replace(/\D/g, '');

        formSetter(prevState => ({
            ...prevState,
            [name]: formattedValue,
        }));
    };

    
    return (
      <div className={style.container}>
        <div className={style.box}>
          <h2>Editar</h2>
          <div className={style.formcontainer}>
            <form className={style.transferform}>
                <label htmlFor="conta">CPF</label>
                <input
                    type="text"
                    id="conta"
                    name="documento"
                    value={form.documento}
                    disabled
                />
                <label htmlFor="conta">Número da Conta:</label>
                <input
                    type="text"
                    id="conta"
                    name="conta"
                    value={form.conta}
                    disabled
                />
                <label htmlFor="conta">Número da Agencia:</label>
                <input
                    type="text"
                    id="conta"
                    name="agencia"
                    value={form.agencia}
                    disabled
                />
                <label htmlFor="conta">limite Pix</label>
                <input
                    type="text"
                    id="conta"
                    name="valor"
                    value={form.valor}
                    onChange={(e) => handleInputChange(e, setForm)}
                />
                <button type="submit" className={style.btn}>Salvar</button>
            </form>
          </div>
          {errorMessage && <p className={style.error}>{errorMessage}</p>}
        </div>
      </div>
    );
  };

export default Edicao;
