import { Link } from 'react-router-dom';
import React, { useState } from 'react';
import '../../Style/Transferencia/Re.css';

const Transferencia = () => {
    const [form1, setForm1] = useState({ documento: '', agencia: '', conta: '', valor: '' });
    const [form2, setForm2] = useState({ documento: '', agencia: '', conta: '', valor: '' });
  
    const handleInputChange = (e, formSetter) => {
      const { name, value } = e.target;
      let formattedValue = value.replace(/\D/g, '');
  
      if (name.includes('documento')) {
        formattedValue = formatCPF(formattedValue);
      }
  
      formSetter(prevState => ({
        ...prevState,
        [name]: formattedValue,
      }));
    };
  
    const formatCPF = (value) => {
      value = value.substring(0, 11); // Limita ao tamanho do CPF
  
      if (value.length > 9) {
        value = value.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
      } else if (value.length > 6) {
        value = value.replace(/(\d{3})(\d{3})(\d{3})/, '$1.$2.$3');
      } else if (value.length > 3) {
        value = value.replace(/(\d{3})(\d{3})/, '$1.$2');
      }
  
      return value;
    };
  
    return (
      <div className="container">
        <div className="box">
          <h2>Realizar Transação</h2>
          <div className="form-container">
            <form className="transfer-form">
              <label htmlFor="documento1">Documento:</label>
              <input
                type="text"
                id="documento1"
                name="documento"
                value={form1.documento}
                onChange={(e) => handleInputChange(e, setForm1)}
              />
  
              <label htmlFor="agencia1">Número da Agência:</label>
              <input
                type="text"
                id="agencia1"
                name="agencia"
                value={form1.agencia}
                onChange={(e) => handleInputChange(e, setForm1)}
              />
  
              <label htmlFor="conta1">Número da Conta:</label>
              <input
                type="text"
                id="conta1"
                name="conta"
                value={form1.conta}
                onChange={(e) => handleInputChange(e, setForm1)}
              />
  
              <label htmlFor="valor1">Valor da Transferência:</label>
              <input
                type="text"
                id="valor1"
                name="valor"
                value={form1.valor}
                onChange={(e) => handleInputChange(e, setForm1)}
              />
            </form>
  
            <form className="transfer-form">
              <label htmlFor="documento2">Documento:</label>
              <input
                type="text"
                id="documento2"
                name="documento"
                value={form2.documento}
                onChange={(e) => handleInputChange(e, setForm2)}
              />
  
              <label htmlFor="agencia2">Número da Agência:</label>
              <input
                type="text"
                id="agencia2"
                name="agencia"
                value={form2.agencia}
                onChange={(e) => handleInputChange(e, setForm2)}
              />
  
              <label htmlFor="conta2">Número da Conta:</label>
              <input
                type="text"
                id="conta2"
                name="conta"
                value={form2.conta}
                onChange={(e) => handleInputChange(e, setForm2)}
              />
  
              <label htmlFor="valor2">Valor da Transferência:</label>
              <input
                type="text"
                id="valor2"
                name="valor"
                value={form2.valor}
                onChange={(e) => handleInputChange(e, setForm2)}
              />
            </form>
          </div>
          <Link to="/Resultado"><button type="submit" className="btn">Enviar</button></Link>
        </div>
      </div>
    );
  };

export default Transferencia;
