import React, { useState } from 'react';
import './css/Relatorio.css';

const Relatorio = () => {
  const [form1, setForm1] = useState({ documento: '', agencia: '', conta: '', valor: '' });
  const [form2, setForm2] = useState({ documento: '', agencia: '', conta: '', valor: '' });
  const [records, setRecords] = useState([
    { id: 1, documento: '123.456.789-09', agencia: '1234', conta: '56789-0', valor: '1000' },
    { id: 2, documento: '987.654.321-00', agencia: '4321', conta: '98765-4', valor: '2000' },
  ]);

  const handleInputChange = (e, formSetter) => {
    const { name, value } = e.target;
    let formattedValue = value.replace(/\D/g, '');

    if (name.includes('documento')) {
      formattedValue = formatCPF(formattedValue);
    }

    formSetter(prevState => ({
      ...prevState,
      [name]: formattedValue,
    }));
  };

  const formatCPF = (value) => {
    value = value.substring(0, 11); // Limita ao tamanho do CPF

    if (value.length > 9) {
      value = value.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
    } else if (value.length > 6) {
      value = value.replace(/(\d{3})(\d{3})(\d{3})/, '$1.$2.$3');
    } else if (value.length > 3) {
      value = value.replace(/(\d{3})(\d{3})/, '$1.$2');
    }

    return value;
  };

  const allowOnlyNumbers = (input) => {
    return input.replace(/\D/g, ''); // Remove todos os caracteres não numéricos
  };

  const handleDelete = (id) => {
    setRecords(records.filter(record => record.id !== id));
  };

  const handleEdit = (id) => {
    // Lógica de edição aqui
    alert(`Editar registro com ID ${id}`);
  };

  return (
    <div className="container">
      <div className="box">
        <h2>Realizar Transação</h2>
        <div className="form-container">
          <form className="transfer-form">
            <label htmlFor="documento1">Documento:</label>
            <input
              type="text"
              id="documento1"
              name="documento"
              value={form1.documento}
              onChange={(e) => handleInputChange(e, setForm1)}
            />

            <label htmlFor="agencia1">Número da Agência:</label>
            <input
              type="text"
              id="agencia1"
              name="agencia"
              value={form1.agencia}
              onChange={(e) => handleInputChange(e, setForm1)}
            />

            <label htmlFor="conta1">Número da Conta:</label>
            <input
              type="text"
              id="conta1"
              name="conta"
              value={form1.conta}
              onChange={(e) => handleInputChange(e, setForm1)}
            />

            <label htmlFor="valor1">Valor da Transferência:</label>
            <input
              type="text"
              id="valor1"
              name="valor"
              value={form1.valor}
              onChange={(e) => handleInputChange(e, setForm1)}
            />
          </form>

          <form className="transfer-form">
            <label htmlFor="documento2">Documento:</label>
            <input
              type="text"
              id="documento2"
              name="documento"
              value={form2.documento}
              onChange={(e) => handleInputChange(e, setForm2)}
            />

            <label htmlFor="agencia2">Número da Agência:</label>
            <input
              type="text"
              id="agencia2"
              name="agencia"
              value={form2.agencia}
              onChange={(e) => handleInputChange(e, setForm2)}
            />

            <label htmlFor="conta2">Número da Conta:</label>
            <input
              type="text"
              id="conta2"
              name="conta"
              value={form2.conta}
              onChange={(e) => handleInputChange(e, setForm2)}
            />

            <label htmlFor="valor2">Valor da Transferência:</label>
            <input
              type="text"
              id="valor2"
              name="valor"
              value={form2.valor}
              onChange={(e) => handleInputChange(e, setForm2)}
            />
          </form>
        </div>
      </div>
      <button type="submit" className="btn">Enviar</button>

      <div className="table-container">
        <h2>Registros de Transações</h2>
        <table className="records-table">
          <thead>
            <tr>
              <th>Documento</th>
              <th>Número da Agência</th>
              <th>Número da Conta</th>
              <th>Valor da Transferência</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {records.map((record) => (
              <tr key={record.id}>
                <td>{record.documento}</td>
                <td>{record.agencia}</td>
                <td>{record.conta}</td>
                <td>{record.valor}</td>
                <td>
                  <button onClick={() => handleEdit(record.id)} className="action-btn">Editar</button>
                  <button onClick={() => handleDelete(record.id)} className="action-btn">Deletar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Relatorio;
