import { Link  } from 'react-router-dom';
import React, { useState } from 'react';
import style from '../../Style/Transferencia/Transferencia.module.css';

const Transferencia = () => {
    const [form1, setForm1] = useState({ documento: '', agencia: '', conta: '', valor: '' });
    const [form2, setForm2] = useState({ documento: '', agencia: '', conta: '', valor: '' });
    const [errorMessage, setErrorMessage] = useState('');
    //const history = useHistory();

    const handleInputChange = (e, formSetter) => {
        const { name, value } = e.target;
        let formattedValue = value.replace(/\D/g, '');

        formSetter(prevState => ({
            ...prevState,
            [name]: formattedValue,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('SUA_API_URL', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    contaOrigem: form1.conta,
                    contaDestino: form2.conta,
                    valor: form2.valor
                })
            });

            if (!response.ok) {
                throw new Error('Erro ao consultar a API');
            }

            const result = await response.json();
            if (result.saldoSuficiente) {
                history.push({
                    pathname: '/Resultado',
                    state: { mensagem: 'Transferência concluída com sucesso!' }
                });
            } else {
                setErrorMessage('Saldo insuficiente para realizar a transferência.');
            }
        } catch (error) {
            setErrorMessage('Ocorreu um erro ao tentar realizar a transferência.');
        }
    };
    return (
      <div className={style.container}>
        <div className={style.box}>
          <h2>Realizar Transação</h2>
          <div className={style.formcontainer}>
            <p>De:</p>
            <form className={style.transferform}>
              <label htmlFor="conta1">Número da Conta:</label>
              <input
                type="text"
                id="conta1"
                name="conta"
                value={form1.conta}
                onChange={(e) => handleInputChange(e, setForm1)}
              />
            </form>
            <p>Para:</p>
            <form className={style.transferform}>
  
              <label htmlFor="conta2">Número da Conta:</label>
              <input
                type="text"
                id="conta2"
                name="conta"
                value={form2.conta}
                onChange={(e) => handleInputChange(e, setForm2)}
              />
  
              <label htmlFor="valor2">Valor da Transferência:</label>
              <input
                type="text"
                id="valor2"
                name="valor"
                value={form2.valor}
                onChange={(e) => handleInputChange(e, setForm2)}
              />
            </form>
          </div>
          <Link to="/Resultado" className={style.link}><button type="submit" className={style.btn}>Enviar</button></Link>
        </div>
      </div>
    );
  };

export default Transferencia;
