import React, { useState } from 'react';
import './css/Relatorio.css';

const Relatorio = () => {
    const [records, setRecords] = useState([]);

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:8080/transacao/conta');
            const data = await response.json();
            setRecords(data); 
        } catch (error) {
            console.error('Erro ao buscar dados da API:', error);
        }
    };

  
    const handleDelete = (id) => {
      setRecords(records.filter(record => record.id !== id));
    };
  
    const handleEdit = (id) => {
      // Lógica de edição aqui
      alert(`Editar registro com ID ${id}`);
    };
  
    return (
        <div className="container">
            <div className="box">
            <h2>Registros de Transações</h2>
            <div className="table-container">
                <table className="records-table">
                <thead>
                    <tr>
                    <th>Documento</th>
                    <th>Número da Agência</th>
                    <th>Número da Conta</th>
                    <th>Valor da Transferência</th>
                    <th>Ações</th>
                    </tr>
                </thead>
                <tbody>
                    {records.map((record) => (
                    <tr key={record.id}>
                        <td>{record.documento}</td>
                        <td>{record.agencia}</td>
                        <td>{record.conta}</td>
                        <td>{record.valor}</td>
                        <td>
                        <button onClick={() => handleEdit(record.id)} className="action-btn">Editar</button>
                        <button onClick={() => handleDelete(record.id)} className="action-btn">Deletar</button>
                        </td>
                    </tr>
                    ))}
                </tbody>
                </table>
            </div>
            </div>
        </div>
    );
  };

export default Relatorio;
