import './css/Transferencia.css';



function Transferencia() {
    function formatCPF(input) {
        let value = input.value.replace(/\D/g, ''); // Remove todos os caracteres não numéricos
        value = value.substring(0, 11); // Limita ao tamanho do CPF

        if (value.length > 9) {
            value = value.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, '$1.$2.$3-$4');
        } else if (value.length > 6) {
            value = value.replace(/(\d{3})(\d{3})(\d{3})/, '$1.$2.$3');
        } else if (value.length > 3) {
            value = value.replace(/(\d{3})(\d{3})/, '$1.$2');
        }

        input.value = value;
    }

    function allowOnlyNumbers(input) {
        input.value = input.value.replace(/\D/g, ''); // Remove todos os caracteres não numéricos
    }
  return (
    <div className="Transferencia">
        <body>
            <div class="container">
                <div class="box">
                    <h2>Realizar Transferencia</h2>
                    <div class="form-container">
                        <form class="transfer-form">
                            <label for="documento1">CPF:</label>
                            <input type="text" id="documento1" name="documento1" />

                            <label for="agencia1">Número da Agência:</label>
                            <input type="text" id="agencia1" name="agencia1" />

                            <label for="conta1">Número da Conta:</label>
                            <input type="text" id="conta1" name="conta1" />

                            <label for="valor1">Valor da Transferência:</label>
                            <input type="text" id="valor1" name="valor1" />
                        </form>
                    
                        <form class="transfer-form">
                            <label for="documento2">CPF:</label>
                            <input type="text" id="documento2" name="documento2" />

                            <label for="agencia2">Número da Agência:</label>
                            <input type="text" id="agencia2" name="agencia2" />

                            <label for="conta2">Número da Conta:</label>
                            <input type="text" id="conta2" name="conta2" />

                            <label for="valor2">Valor da Transferência:</label>
                            <input type="text" id="valor2" name="valor2" />
                        </form>
                    </div>
                    <button type="submit" class="btn">Enviar</button>
                </div>
            </div>
        </body>
    </div>
  );
}

export default Transferencia;
